});		});				$('#story_list').click(function(e)		{		  if ($(e.target).is('.add_comment'))		  {		    var $comment_box = $("<li class='textElement editing'><div class='editingDiv'><form class='formTextElement'><textarea class='inputEditor' name='input'></textarea></form><div class='belowTextEdit'><div class='actions' style='padding-left:255px;'><button class='cancel small cancelEditor' type='reset'>Cancel</button><button class='submit small blue submitComment' type='submit'>Done</button></div></div></div></li><li class='addTextElementAnchor'><span><a><img class='add_comment' src='/Storify/img/editcomment.png' border='0'/></a></span></li>");		    $(e.target).closest('li').after($comment_box);			$(".inputEditor").cleditor({			width:370,			height:200,			controls:"bold italic underline strikethrough link | font size",						});			//$(e.target).closest('#input').cleditor();		  }		  		  if($(e.target).is('.cancelEditor'))		  {			$(e.target).closest('.textElement').next('.addTextElementAnchor').remove();			$(e.target).closest('.textElement').remove();		  }		  		  if($(e.target).is('.submitComment'))		  {			//debugger;			var $textElement = $(e.target).closest('.textElement');			var comment = $textElement.find('.inputEditor').val();			$(e.target).closest('.editingDiv').remove();			var $commentDiv = $("<div class='commentBox'>"+comment+"</div>");			$textElement.removeClass('editing').addClass('editted').append($commentDiv);		  }		});					  	});</script><?php$content = "<div class='div_center'><div id='actions' style='margin-left:20px;'><span><a id='draftBtn' href='./' >保存草稿</a></span><span style='margin-left:30px;'><a id='previewBtn' href='./' >预览</a></span><span style='margin-left:30px;'><a id='publishBtn' href='./' >发布</a></span></div><div class='content'>  <div class='inner'>	<div id='source_pane' class='float_l showborder'>	  <div id='source_controller'>	  </div>	  <div id='sourcelist_container'>	    <div id='tabs'>		  <ul