<?phpinclude "../global.php";session_start();include_once( '../weibo/config.php' );include_once( '../weibo/weibooauth.php' );?><script type="text/javascript" src="/storify/js/jquery.embedly.min.js"></script><?phpif(isset($_GET['post_id']) && !isset($_GET['action'])){	$c = new WeiboClient( WB_AKEY , WB_SKEY , $_SESSION['last_key']['oauth_token'] , $_SESSION['last_key']['oauth_token_secret']  );	$post_id = $_GET['post_id'];	$result = $DB->fetch_one_array("select * from ".$db_prefix."posts where ID='".$post_id."'");	if(!$result)	{	  throw new Exception('Could not execute query.');	}	$story_title=$result['post_title'];	$story_summary=$result['post_summary'];	$story_status=$result['post_status'];	$story_content=$result['post_content'];	$story_content_array = json_decode($story_content, true);		if(0 == strcmp($story_status, 'Published'))	{	  $content = "<div class='div_center'><div id='publish_container' class='showborder'>			  <div id='story_action'><span>".$story_status."</span><span class='float_r'><a href='#'>通告			  </a> | <a href='/Storify/member/user.php?post_id=".$post_id."&action=remove'>删除</a> | <a href='/Storify/member/user.php?post_id=".$post_id."&action=edit'>编辑</a></span></div>";	}	else	{	  $content = "<div class='div_center'><div id='publish_container' class='showborder'>			  <div id='story_action'><span>".$story_status."</span><span class='float_r'><a href='/Storify/member/user.php?post_id=".$post_id."&action=remove'>删除			  </a> | <a href='/Storify/member/user.php?post_id=".$post_id."&action=edit'>编辑</a> | <a href='/Storify/member/user.php?post_id=".$post_id."&action=publish'>发布</a></span></div>";	}		$content .="<div style='padding-left:20px;'><h2>".$story_title."</h2></div>			  <div style='padding-left:20px;'>".$_SESSION['username']."</div>			  <div style='padding-left:20px; border-bottom:1px solid #C9C9C9;'>".$story_summary."</div>			  <ul id='weibo_ul' style='padding:0;'>";		foreach($story_content_array['content'] as $key=>$val)	{	  if($val['type'] === 'weibo')	  {	    $weibo_per_id = $val['content'];		$single_weibo  = $c->show_status($weibo_per_id );				if ($single_weibo === false || $single_weibo === null){		echo "Error occured";		return false;		}		if (isset($single_weibo['error_code']) && isset($single_weibo['error'])){			echo ('Error_code: '.$single_weibo['error_code'].';  Error: '.$single_weibo['error'] );			return false;		}		if (isset($single_weibo['id']) && isset($single_weibo['text'])){			$createTime = dateFormat($single_weibo['created_at']);			$content .="<li class='weibo_drop' id='$weibo_per_id' style='border:none;'><div class='story_wrapper'><div><span class='weibo_text'>".$single_weibo['text']."</span></div>			<div id='story_signature'><div style='float:right;'><a href='http://weibo.com/".$single_weibo['user']['id']."' target='_blank'>			<img class='profile_img' style='width: 32px; height: 32px; overflow: hidden; margin-top:2px;' src='".$single_weibo['user']['profile_image_url']."' alt='".$single_weibo['user']['screen_name']."' border=0 /></a></div>			<div id='signature_text' style='float:right; margin-right:5px;'><a class='weibo_from' href='http://weibo.com/".$single_weibo['user']['id']."' target='_blank' style='display:block; height:16px;'>			<span>".$single_weibo['user']['screen_name']."</span></a><span class='weibo_date' style='display:block; height:16px;'>".$createTime."</span></div></div></div></li>";		}	  }	  else if($val['type'] === 'comment')	  {	    $comment_text = $val['content'];		$content .="<li class='textElement'><div class='commentBox'>".$comment_text."</div></li>";		  }	  else if($val['type'] === 'video')	  {	    $video_url = $val['content'];		$content .="<li class='video_element'><div><a class='videoTitle' target='_blank' href='".$video_url."'></a></div></li>";		  }	}		$content .="</ul><div style='display: block; padding:0 10px 0 5px; text-align:right;'>Powered by <a name='poweredby' target='_blank' href='http://storybing.com'>StoryBing</a></div></div></div>";	echo $content;	/*echo "<script language='javascript' >			window.onload = function()			{			  $('.weibo_drop').each(function(){			  var weibo_permanent_id = $(this).attr('id');			  get_weibo_by_id(weibo_permanent_id);			  });			  $('.weibo_drop').css({'margin':'auto', 'width':'60%', 'margin-top':'10px', 'border':'none'});			  			  $('.video_element').each(function(){			  var videoUrl = $(this).find('.videoTitle').attr('href');			  append_video_content(videoUrl);			  });			}			</script>";*/}else if(isset($_GET['post_id']) && isset($_GET['action'])){	$story_id = $_GET['post_id'];	$story_action = $_GET['action'];	if(0 == strcmp($story_action, 'remove'))	{	  $result=$DB->query("DELETE FROM ".$db_prefix."posts where ID='".$story_id."'");	  go($rooturl.'/member/user.php');	}	else if(0 == strcmp($story_action, 'edit'))	{	  go($rooturl.'/member/index.php?post_id='.$story_id);	}	else if(0 == strcmp($story_action, 'publish'))	{	  $result=$DB->query("update ".$db_prefix."posts set post_status='Published'  WHERE ID='".$story_id."'");	  go($rooturl.'/member/user.php?post_id='.$story_id);	}	else	{	  throw new Exception('Undefined story action.');	}}else{  $story_content = "<div class='div_center'><div id='story_list'><ul>";  $result=$DB->query("SELECT * FROM ".$db_prefix."posts where post_author='".$_SESSION['uid']."'");  while ($story_item = mysql_fetch_array($result))  {    //printf ("title: %s  summary: %s", $story_item['post_title'], $story_item['post_summary']);	$post_title = $story_item['post_title'];    $story_content .= "<li><a href='/Storify/member/user.php?post_id=".$story_item['ID']."'><img src='/Storify/img/me.jpg' width='200' height='150' />".$post_title."</a></li>";  }  $story_content .="</ul></div></div>";  echo $story_content;}?><script type="text/javascript">function append_video_content(url){  $.embedly(url, {key: '4ac512dca79011e0aeec4040d3dc5c07', maxWidth: 420, wrapElement: 'div', method : 'afterParent'  }, function(oembed){					if (oembed != null)	{	  var videoContent = oembed.code;	  $("a[href="+url+"]").parent().after(videoContent);	}		  			  });}$(function(){	$('#user_action').css('display', 'inline');	$('#publish_container').css({'margin':'auto', 'width':'80%'});	//$('.weibo_drop').css({'margin':'auto', 'width':'60%', 'margin-top':'10px', 'border':'none'});});	</script><?phpinclude "../include/footer.htm";?>